using System;
using System.Collections.Generic;
using System.Text;
using System.Text.RegularExpressions;

namespace TM_Comms
{
    public partial class EthernetSlave
    {
        public enum Headers
        {
            TMSVR, //External Script
            CPERR  //Communication Content error
        }
        public enum ErrorCodes
        {
            OK = 0,
            NOT_SUPPORTED,
            WRITE_PERMISSION,
            INVALID_DATA,

            NOT_EXIST,
            READ_ONLY,
            MODE_ERROR,
            VALUE_ERROR
        }
        public enum Modes
        {
            RESPONSE,
            BINARY,
            STRING,
            JSON,
            DIAG_ONLY = 99
        }
        public enum CPErrorCodes
        {
            OK = 0,
            PACKET_ERROR,
            CHECKSUM_ERROR,
            HEADER_ERROR,
            PACKET_DATA_ERROR,
        }
        public const string StartChar = "$";
        public const string Separator = ",";
        public const string ChecksumChar = "*";
        public const string EndChar = "\r\n";

        public EthernetSlave() { }
        public EthernetSlave(string content, Headers header = Headers.TMSVR, string transactionID = "local", Modes mode = Modes.STRING)
        {
            Content = content;
            Header = header;
            TransactionID = transactionID;
            Mode = mode;
        }

        public bool ParseMessage(string message)
        {
            //Server Response
            if (!Regex.IsMatch(message, @"^[$].+\*[0-9A-F][0-9A-F]"))
                return false;

            Match match;
            if (Regex.IsMatch(message, @"^[$]CPERR"))
            {
                string content = Regex.Replace(message, @"^[$]\w*,\w*,", "");
                Content = Regex.Replace(content, @",[*][0-9A-Z][0-9A-Z]*", "");


                match = Regex.Match(message, @"^[$]\w*,\w*,\w*,");
            }
            else
            {
                string content = Regex.Replace(message, @"^[$]\w*,\w*,\w*,\w*,", "");
                Content = Regex.Replace(content, @",[*][0-9A-Z][0-9A-Z]*", "");

                match = Regex.Match(message, @"^[$]\w*,\w*,\w*,\w*,\w*,");
            }

            if (match.Success)
            {
                string[] spl = match.Value.Split(',');

                if (Enum.TryParse(spl[0].TrimStart('$'), out Headers header))
                    Header = header;
                else
                    return false;

                if (Header == Headers.CPERR)
                    if (Enum.TryParse(spl[2], out CPErrorCodes cperror))
                        CPErrorCode = cperror;
                    else
                        return false;
                else
                {
                    TransactionID = spl[2];

                    if (Enum.TryParse(spl[3], out Modes mode))
                        Mode = mode;
                    else
                        return false;

                    if(Mode == Modes.RESPONSE)
                        if (Enum.TryParse(spl[4], out ErrorCodes error))
                            ErrorCode = error;
                        else
                            return false;

                }

                return true;
            }

            return false;

        }


        public Headers Header { get; set; } 
        public string HeaderString => Header.ToString();

        public int Length => Data.Length;

        public CPErrorCodes CPErrorCode { get; private set; } = CPErrorCodes.OK;
        public ErrorCodes ErrorCode { get; private set; } = ErrorCodes.OK;

        public string TransactionID { get; set; } = "local";
        public int TransactionID_Int
        {
            get
            {
                if (int.TryParse(TransactionID, out int res))
                    return res;
                return -1;
            }
        }

        public Modes Mode { get; set; } = Modes.STRING;

        public string Content { get; set; } = string.Empty;

        private string Data => $"{TransactionID}{Separator}{Mode:D}{Separator}{Content}";

        public byte Checksum => CalCheckSum();
        public string ChecksumString => CalCheckSum().ToString("X2");
        public string Message => $"{StartChar}{HeaderString}{Separator}{Length}{Separator}{Data}{Separator}{ChecksumChar}{ChecksumString}{EndChar}";

        private byte CalCheckSum()
        {
            Byte _CheckSumByte = 0x00;
            Byte[] bData = Encoding.ASCII.GetBytes($"{HeaderString}{Separator}{Length}{Separator}{Data}{Separator}");
            for (int i = 0; i < bData.Length; i++)
                _CheckSumByte ^= bData[i];
            return _CheckSumByte;
        }
    }

